IF OBJECT_ID(N'[__EFMigrationsHistory]') IS NULL
BEGIN
    CREATE TABLE [__EFMigrationsHistory] (
        [MigrationId] nvarchar(150) NOT NULL,
        [ProductVersion] nvarchar(32) NOT NULL,
        CONSTRAINT [PK___EFMigrationsHistory] PRIMARY KEY ([MigrationId])
    );
END;
GO

BEGIN TRANSACTION;
GO

CREATE TABLE [Jogos] (
    [Id] int NOT NULL IDENTITY,
    [Nome] nvarchar(max) NULL,
    [Descricao] nvarchar(max) NULL,
    [Genero] nvarchar(max) NULL,
    [DataL] datetime2 NOT NULL,
    [Desenvolvedor] nvarchar(max) NULL,
    CONSTRAINT [PK_Jogos] PRIMARY KEY ([Id])
);
GO

CREATE TABLE [Usuarios] (
    [Id] int NOT NULL IDENTITY,
    [Username] nvarchar(max) NULL,
    [PasswordHash] varbinary(max) NULL,
    [PasswordSalt] varbinary(max) NULL,
    [Latitude] float NULL,
    [Longitude] float NULL,
    [DataAcesso] datetime2 NULL,
    [PasswordString] nvarchar(max) NULL,
    [Perfil] nvarchar(max) NULL DEFAULT N'Usuario',
    [Email] nvarchar(max) NULL,
    CONSTRAINT [PK_Usuarios] PRIMARY KEY ([Id])
);
GO

CREATE TABLE [Favoritos] (
    [Id] int NOT NULL IDENTITY,
    [JogoId] int NOT NULL,
    [UsuarioId] int NOT NULL,
    CONSTRAINT [PK_Favoritos] PRIMARY KEY ([Id]),
    CONSTRAINT [FK_Favoritos_Jogos_JogoId] FOREIGN KEY ([JogoId]) REFERENCES [Jogos] ([Id]) ON DELETE CASCADE,
    CONSTRAINT [FK_Favoritos_Usuarios_UsuarioId] FOREIGN KEY ([UsuarioId]) REFERENCES [Usuarios] ([Id]) ON DELETE CASCADE
);
GO

CREATE TABLE [JogoUsuario] (
    [JogoId] int NOT NULL,
    [UsuarioId] int NOT NULL,
    CONSTRAINT [PK_JogoUsuario] PRIMARY KEY ([JogoId], [UsuarioId]),
    CONSTRAINT [FK_JogoUsuario_Jogos_JogoId] FOREIGN KEY ([JogoId]) REFERENCES [Jogos] ([Id]) ON DELETE CASCADE,
    CONSTRAINT [FK_JogoUsuario_Usuarios_UsuarioId] FOREIGN KEY ([UsuarioId]) REFERENCES [Usuarios] ([Id]) ON DELETE CASCADE
);
GO

IF EXISTS (SELECT * FROM [sys].[identity_columns] WHERE [name] IN (N'Id', N'DataL', N'Descricao', N'Desenvolvedor', N'Genero', N'Nome') AND [object_id] = OBJECT_ID(N'[Jogos]'))
    SET IDENTITY_INSERT [Jogos] ON;
INSERT INTO [Jogos] ([Id], [DataL], [Descricao], [Desenvolvedor], [Genero], [Nome])
VALUES (1, '2021-10-07T00:00:00.0000000', N'Far Cry 6 é um jogo de tiro em primeira pessoa de mundo aberto desenvolvido pela Ubisoft Toronto. Ambientado na ilha fictícia de Yara, os jogadores assumem o papel de Dani Rojas, um guerrilheiro lutando contra o regime opressivo do ditador Anton Castillo. Com um vasto mundo para explorar, combates intensos e uma narrativa envolvente, Far Cry 6 oferece uma experiência cheia de ação e aventura.', N'Ubisoft Toronto', N'Tiro, Ação', N'Far Cry 6'),
(2, '2018-06-14T00:00:00.0000000', N'Bloons TD6 é um jogo de estratégia em que os jogadores devem defender seu território contra ondas de balões invasores. O jogo apresenta torres de defesa com habilidades únicas, diversos mapas e modos de jogo, oferecendo uma experiência desafiadora e divertida.', N'Ninja Kiwi', N'Estratégia', N'Bloons TD6'),
(3, '2010-05-18T00:00:00.0000000', N'Red Dead Redemption é um jogo de ação e aventura em mundo aberto ambientado no Velho Oeste. Os jogadores assumem o papel de um pistoleiro chamado John Marston, que deve caçar criminosos e enfrentar desafios em uma história épica. O jogo oferece um vasto mundo para explorar, com muitas missões e atividades para realizar.', N'Rockstar San Diego', N'Ação, Aventura', N'Red Dead Redemption'),
(4, '2020-06-02T00:00:00.0000000', N'Valorant é um jogo de tiro tático em primeira pessoa desenvolvido pela Riot Games. No jogo, duas equipes competem em partidas de ataque e defesa, utilizando personagens com habilidades únicas. Com combates estratégicos e trabalho em equipe, Valorant oferece uma experiência de jogo emocionante e competitiva.', N'Riot Games', N'Tiro em primeira pessoa', N'Valorant'),
(5, '2012-08-21T00:00:00.0000000', N'Counter-Strike: Global Offensive é um jogo de tiro em primeira pessoa competitivo. Os jogadores se juntam a equipes terroristas ou antiterroristas e participam de partidas intensas em diversos modos de jogo. Com uma jogabilidade estratégica e habilidades individuais em teste, CS:GO continua sendo um dos jogos mais populares do gênero.', N'Valve', N'Tiro em primeira pessoa', N'Counter-Strike: Global Offensive'),
(6, '2009-10-27T00:00:00.0000000', N'League of Legends é um jogo online de batalha multiplayer em arenas (MOBA). Duas equipes de campeões únicos se enfrentam em intensas batalhas, com o objetivo de destruir o nexus inimigo. Com estratégia, trabalho em equipe e habilidades individuais, League of Legends é um dos jogos mais populares e competitivos do mundo.', N'Riot Games', N'MOBA', N'League of Legends'),
(7, '2013-07-09T00:00:00.0000000', N'Dota 2 é um jogo online de batalha multiplayer em arenas (MOBA) desenvolvido pela Valve. Os jogadores formam equipes e batalham para destruir o Ancião inimigo, com a ajuda de heróis poderosos e estratégias elaboradas. Com uma grande variedade de heróis e uma comunidade ativa, Dota 2 oferece uma experiência desafiadora e competitiva.', N'Valve', N'MOBA', N'Dota 2'),
(8, '2011-11-18T00:00:00.0000000', N'Minecraft é um jogo de construção e aventura em um mundo sandbox. Os jogadores podem explorar, construir estruturas e objetos, coletar recursos e enfrentar criaturas em um ambiente pixelizado. Com sua jogabilidade criativa e possibilidades infinitas, Minecraft se tornou um fenômeno mundial.', N'Mojang Studios', N'Sandbox, Aventura', N'Minecraft'),
(9, '2013-09-17T00:00:00.0000000', N'Grand Theft Auto V é um jogo de ação e aventura em mundo aberto desenvolvido pela Rockstar North. Nele, os jogadores podem explorar a cidade fictícia de Los Santos, realizar missões, participar de atividades e interagir com personagens diversos.', N'Rockstar North', N'Ação, Aventura', N'Grand Theft Auto V'),
(10, '2017-07-25T00:00:00.0000000', N'Fortnite é um jogo de tiro em terceira pessoa com elementos de construção. Os jogadores são colocados em um mapa onde devem lutar contra outros jogadores, enquanto constroem estruturas e procuram por recursos. Com modos de jogo variados e eventos frequentes, Fortnite se tornou um dos jogos mais populares do mundo.', N'Epic Games', N'Battle Royale, Construção', N'Fortnite'),
(11, '2004-11-23T00:00:00.0000000', N'World of Warcraft é um MMORPG ambientado no mundo de fantasia de Azeroth. Os jogadores podem criar personagens, explorar vastas terras, realizar missões, participar de batalhas épicas e interagir com outros jogadores. Com uma história rica e constantes atualizações, World of Warcraft continua sendo um dos jogos mais populares do gênero.', N'Blizzard Entertainment', N'MMORPG', N'World of Warcraft'),
(12, '2014-09-02T00:00:00.0000000', N'The Sims 4 é um jogo de simulação de vida em que os jogadores podem criar e controlar personagens virtuais, chamados Sims. Eles podem construir casas, desenvolver relacionamentos, buscar carreiras e viver diversas experiências. Com sua jogabilidade cativante e ferramentas de criação, The Sims 4 é um dos jogos mais populares da franquia.', N'Maxis', N'Simulação', N'The Sims 4'),
(13, '2020-03-10T00:00:00.0000000', N'Call of Duty: Warzone é um jogo de battle royale gratuito que faz parte da franquia Call of Duty. Os jogadores são lançados em um mapa vasto onde devem lutar contra outros jogadores para serem o último sobrevivente. Com um combate intenso e diversas armas e veículos, Warzone oferece uma experiência emocionante.', N'Infinity Ward', N'Battle Royale, Tiro', N'Call of Duty: Warzone'),
(14, '2020-06-02T00:00:00.0000000', N'Valorant é um jogo de tiro tático em equipe desenvolvido pela Riot Games. Os jogadores participam de partidas 5v5 onde devem completar objetivos e eliminar o time adversário. Com personagens únicos e habilidades especiais, Valorant combina estratégia e habilidade individual.', N'Riot Games', N'Tiro, Estratégia', N'Valorant'),
(15, '2019-02-04T00:00:00.0000000', N'Apex Legends é um jogo de tiro em equipe no estilo battle royale ambientado no universo do jogo Titanfall. Os jogadores formam esquadrões e competem contra outros times em um mapa repleto de ação. Com personagens únicos, habilidades especiais e um combate dinâmico, Apex Legends conquistou uma grande base de fãs.', N'Respawn Entertainment', N'Battle Royale, Tiro', N'Apex Legends'),
(16, '2018-11-20T00:00:00.0000000', N'Battlefield V é um jogo de tiro em primeira pessoa ambientado na Segunda Guerra Mundial. Os jogadores participam de batalhas multiplayer épicas em cenários históricos, além de vivenciar uma campanha para um jogador emocionante. Com um foco em combate realista e uma ampla variedade de modos de jogo, Battlefield V oferece uma experiência de guerra imersiva.', N'DICE', N'Tiro', N'Battlefield V'),
(17, '1993-12-15T00:00:00.0000000', N'FIFA é uma série de jogos de simulação de futebol desenvolvida pela EA Sports. Os jogadores podem controlar times e competir em partidas de futebol realistas, com gráficos impressionantes e uma jogabilidade envolvente. Com licenças oficiais de jogadores, times e ligas, FIFA oferece uma experiência autêntica para os fãs de futebol.', N'EA Vancouver', N'Esporte, Simulação', N'FIFA'),
(18, '2016-05-24T00:00:00.0000000', N'Overwatch é um jogo de tiro em equipe desenvolvido pela Blizzard Entertainment. Os jogadores formam times e participam de intensas batalhas multiplayer em um futuro próximo. Com uma variedade de heróis com habilidades únicas, Overwatch combina ação frenética, estratégia e trabalho em equipe.', N'Blizzard Entertainment', N'Tiro', N'Overwatch'),
(19, '2015-12-01T00:00:00.0000000', N'Rainbow Six Siege é um jogo de tiro tático em equipe desenvolvido pela Ubisoft. Os jogadores assumem o papel de agentes de elite e participam de emocionantes confrontos em equipe. Com um foco em estratégia, comunicação e destruição de ambientes, Rainbow Six Siege oferece uma experiência de jogo intensa e repleta de ação.', N'Ubisoft Montreal', N'Tiro, Estratégia', N'Rainbow Six Siege'),
(20, '2017-12-20T00:00:00.0000000', N'PlayerUnknown''s Battlegrounds, comumente conhecido como PUBG, é um jogo de battle royale onde os jogadores lutam entre si em uma ilha até que reste apenas um sobrevivente. Os jogadores devem encontrar armas, equipamentos e veículos enquanto enfrentam outros jogadores em uma luta pela sobrevivência.', N'PUBG Corporation', N'Battle Royale, Tiro', N'PlayerUnknown''s Battlegrounds (PUBG)'),
(21, '2012-05-15T00:00:00.0000000', N'Diablo III é um jogo de RPG de ação desenvolvido pela Blizzard Entertainment. Os jogadores assumem o papel de um herói lutando contra as forças do mal, enfrentando hordas de inimigos, coletando tesouros e melhorando suas habilidades. Com uma narrativa envolvente e uma jogabilidade viciante, Diablo III é um dos principais jogos do gênero.', N'Blizzard Entertainment', N'RPG, Ação', N'Diablo III'),
(22, '2018-03-27T00:00:00.0000000', N'Far Cry 5 é um jogo de tiro em primeira pessoa de mundo aberto desenvolvido pela Ubisoft. Ambientado em uma região fictícia dos Estados Unidos, os jogadores devem enfrentar um culto religioso perigoso enquanto exploram o mundo aberto e participam de missões emocionantes. Com uma jogabilidade imersiva e gráficos impressionantes, Far Cry 5 oferece uma experiência de ação intensa.', N'Ubisoft Montreal', N'Tiro, Aventura', N'Far Cry 5'),
(23, '2018-10-26T00:00:00.0000000', N'Red Dead Redemption 2 é um jogo de ação e aventura em mundo aberto desenvolvido pela Rockstar Games. Situado no Velho Oeste, os jogadores assumem o papel de um fora da lei enquanto exploram um vasto mundo, realizam missões, interagem com personagens e vivenciam uma história épica. Com gráficos deslumbrantes e uma jogabilidade imersiva, Red Dead Redemption 2 é uma obra-prima dos jogos.', N'Rockstar Games', N'Ação, Aventura', N'Red Dead Redemption 2'),
(24, '2021-05-07T00:00:00.0000000', N'Resident Evil Village é um jogo de terror e sobrevivência desenvolvido pela Capcom. Continuando a história de Resident Evil 7, os jogadores assumem o papel de Ethan Winters em uma vila assustadora e repleta de perigos. Com uma combinação de combate tenso, quebra-cabeças desafiadores e uma atmosfera sinistra, Resident Evil Village oferece uma experiência aterrorizante.', N'Capcom', N'Terror, Sobrevivência', N'Resident Evil Village'),
(25, '2020-12-10T00:00:00.0000000', N'Cyberpunk 2077 é um jogo de RPG de ação ambientado em um futuro distópico. Os jogadores assumem o papel de V, um mercenário em busca de uma implante lendário que concede imortalidade. Com uma cidade vasta e cheia de possibilidades, escolhas morais significativas e uma história envolvente, Cyberpunk 2077 oferece uma experiência única.', N'CD Projekt Red', N'RPG, Ação', N'Cyberpunk 2077'),
(26, '2020-11-10T00:00:00.0000000', N'Assassin''s Creed Valhalla é um jogo de ação e aventura em mundo aberto da série Assassin''s Creed. Situado na era dos vikings, os jogadores assumem o papel de Eivor, um guerreiro viking em busca de um novo lar. Com batalhas épicas, exploração de vastos territórios e uma narrativa rica, Assassin''s Creed Valhalla oferece uma experiência imersiva no mundo viking.', N'Ubisoft Montreal', N'Ação, Aventura', N'Assassin''s Creed Valhalla'),
(27, '2011-11-11T00:00:00.0000000', N'The Elder Scrolls V: Skyrim é um jogo de RPG de ação em mundo aberto desenvolvido pela Bethesda Game Studios. Os jogadores assumem o papel de um personagem personalizável e embarcam em uma jornada épica em Skyrim, um reino de fantasia repleto de aventuras, missões e perigos. Com uma jogabilidade imersiva e uma rica narrativa, Skyrim é um dos jogos mais aclamados de todos os tempos.', N'Bethesda Game Studios', N'RPG, Ação', N'Elder Scrolls V: Skyrim'),
(28, '2016-02-26T00:00:00.0000000', N'Stardew Valley é um jogo de simulação de fazenda desenvolvido por Eric Barone. Os jogadores assumem o papel de um personagem que herda uma fazenda antiga e precisam administrá-la, cultivando plantações, criando animais, explorando a cidade local, participando de eventos e interagindo com os personagens. Com seu estilo visual encantador e jogabilidade relaxante, Stardew Valley oferece uma experiência de fazenda cativante.', N'ConcernedApe', N'Simulação, RPG', N'Stardew Valley'),
(29, '2015-07-07T00:00:00.0000000', N'Rocket League é um jogo de esporte e ação que combina futebol com carros em uma arena. Os jogadores controlam carros equipados com foguetes e devem marcar gols em uma partida frenética. Com modos de jogo variados, competições online e jogabilidade divertida, Rocket League oferece uma experiência multiplayer emocionante.', N'Psyonix', N'Esporte, Ação', N'Rocket League'),
(30, '2010-07-27T00:00:00.0000000', N'StarCraft II é um jogo de estratégia em tempo real desenvolvido pela Blizzard Entertainment. Os jogadores assumem o controle de uma das três facções - Terranos, Protoss ou Zergs - e competem em batalhas estratégicas. Com uma campanha envolvente, modos multiplayer competitivos e ferramentas de criação de mapas, StarCraft II é um dos principais jogos de estratégia do mercado.', N'Blizzard Entertainment', N'Estratégia', N'StarCraft II'),
(31, '2020-09-28T00:00:00.0000000', N'Genshin Impact é um jogo de ação e RPG de mundo aberto desenvolvido pela miHoYo. Os jogadores exploram o mundo de Teyvat, lutam contra inimigos, completam missões, desvendam segredos e embarcam em uma jornada para encontrar os membros perdidos de sua família. Com uma jogabilidade de combate dinâmica, belos visuais e uma história envolvente, Genshin Impact oferece uma experiência de aventura emocionante.', N'miHoYo', N'Ação, RPG', N'Genshin Impact'),
(32, '2018-01-26T00:00:00.0000000', N'Monster Hunter: World é um jogo de ação e RPG desenvolvido pela Capcom. Os jogadores assumem o papel de um caçador e embarcam em missões para caçar e derrotar monstros gigantes em um mundo aberto. Com um vasto arsenal de armas e armaduras, combate tático e um sistema de progressão profundo, Monster Hunter: World oferece uma experiência desafiadora para os fãs de caçadas épicas.', N'Capcom', N'Ação, RPG', N'Monster Hunter: World'),
(33, '2015-05-19T00:00:00.0000000', N'The Witcher 3: Wild Hunt é um jogo de RPG de ação em mundo aberto desenvolvido pela CD Projekt Red. Os jogadores assumem o papel de Geralt de Rivia, um caçador de monstros conhecido como Witcher, e embarcam em uma busca épica em um vasto continente repleto de perigos, escolhas morais e histórias envolventes. Com uma narrativa profunda, combate desafiador e um mundo rico em detalhes, The Witcher 3: Wild Hunt é considerado um dos melhores jogos de todos os tempos.', N'CD Projekt Red', N'RPG, Ação', N'The Witcher 3: Wild Hunt'),
(34, '2019-04-23T00:00:00.0000000', N'Mortal Kombat 11 é um jogo de luta desenvolvido pela NetherRealm Studios. Os jogadores participam de combates intensos e brutais, controlando uma variedade de personagens icônicos da franquia Mortal Kombat. Com gráficos impressionantes, movimentos especiais devastadores e uma história envolvente, Mortal Kombat 11 oferece uma experiência emocionante para os fãs de luta.', N'NetherRealm Studios', N'Luta', N'Mortal Kombat 11'),
(35, '2015-11-10T00:00:00.0000000', N'Fallout 4 é um jogo de RPG de ação desenvolvido pela Bethesda Game Studios. Ambientado em um mundo pós-apocalíptico, os jogadores assumem o papel de um sobrevivente que emerge de um abrigo subterrâneo para explorar e enfrentar os perigos da região de Boston. Com um vasto mundo aberto, escolhas morais impactantes e um sistema de construção de assentamentos, Fallout 4 oferece uma experiência imersiva no universo pós-nuclear.', N'Bethesda Game Studios', N'RPG, Ação', N'Fallout 4'),
(36, '2011-05-16T00:00:00.0000000', N'Terraria é um jogo de ação e aventura com elementos de construção desenvolvido pela Re-Logic. Os jogadores exploram um mundo pixelado em 2D, coletam recursos, constroem estruturas e enfrentam inimigos em diferentes biomas. Com uma jogabilidade sandbox e uma infinidade de itens, armas e chefes para enfrentar, Terraria oferece uma experiência criativa e desafiadora.', N'Re-Logic', N'Ação, Aventura', N'Terraria'),
(37, '2018-01-23T00:00:00.0000000', N'Subnautica é um jogo de exploração e sobrevivência desenvolvido pela Unknown Worlds Entertainment. Os jogadores assumem o papel de um sobrevivente de um acidente espacial e exploram um oceano alienígena repleto de criaturas perigosas e mistérios. Com uma atmosfera imersiva, gráficos impressionantes e uma narrativa cativante, Subnautica oferece uma experiência', N'Unknown Worlds Entertainment', N'Exploração, Sobrevivência', N'Subnautica'),
(38, '2016-04-12T00:00:00.0000000', N'Dark Souls III é um jogo de RPG de ação desenvolvido pela FromSoftware. Situado em um mundo sombrio e repleto de desafios, os jogadores enfrentam inimigos poderosos, chefes intimidadores e exploram cenários detalhados. Com combates estratégicos, uma atmosfera única e uma narrativa envolvente, Dark Souls III proporciona uma experiência intensa e gratificante.', N'FromSoftware', N'RPG, Ação', N'Dark Souls III'),
(39, '2013-03-25T00:00:00.0000000', N'Warframe é um jogo de tiro em terceira pessoa online desenvolvido pela Digital Extremes. Os jogadores assumem o papel de Tennos, guerreiros espaciais que controlam armaduras avançadas conhecidas como Warframes. Com uma jogabilidade cooperativa, combates frenéticos e um sistema de progressão profundo, Warframe oferece uma experiência viciante no universo futurista.', N'Digital Extremes', N'Tiro em terceira pessoa, MMO', N'Warframe'),
(40, '2014-04-04T00:00:00.0000000', N'The Elder Scrolls Online é um MMORPG desenvolvido pela ZeniMax Online Studios. Situado no universo de fantasia de The Elder Scrolls, os jogadores podem explorar vastas terras, completar missões, lutar contra inimigos e interagir com outros jogadores. Com uma história rica, mecânicas de RPG clássicas e um mundo aberto expansivo, The Elder Scrolls Online oferece uma experiência imersiva no reino de Tamriel.', N'ZeniMax Online Studios', N'MMORPG', N'The Elder Scrolls Online'),
(41, '2012-10-19T00:00:00.0000000', N'Euro Truck Simulator 2 é um simulador de caminhões desenvolvido pela SCS Software. Os jogadores assumem o papel de motoristas de caminhões, transportando cargas por toda a Europa. Com gráficos realistas, uma ampla seleção de caminhões e missões desafiadoras, Euro Truck Simulator 2 proporciona uma experiência autêntica de condução de caminhões.', N'SCS Software', N'Simulação', N'Euro Truck Simulator 2'),
(42, '2013-04-23T00:00:00.0000000', N'Don''t Starve é um jogo de sobrevivência desenvolvido pela Klei Entertainment. Os jogadores assumem o papel de Wilson, um cientista preso em um mundo misterioso e perigoso. Eles devem explorar o ambiente, coletar recursos, criar itens e lutar contra criaturas para sobreviver. Com uma estética única, desafios constantes e uma jogabilidade envolvente, Don''t Starve oferece uma experiência cativante de sobrevivência.', N'Klei Entertainment', N'Sobrevivência', N'Don''t Starve');
INSERT INTO [Jogos] ([Id], [DataL], [Descricao], [Desenvolvedor], [Genero], [Nome])
VALUES (43, '2019-11-14T00:00:00.0000000', N'Age of Empires II: Definitive Edition é uma versão remasterizada do clássico jogo de estratégia em tempo real Age of Empires II. Os jogadores constroem e expandem impérios, treinam exércitos e lutam contra outras civilizações em batalhas épicas. Com gráficos aprimorados, conteúdo adicional e melhorias de jogabilidade, Age of Empires II: Definitive Edition oferece uma experiência nostálgica e aprofundada para os fãs do gênero.', N'Forgotten Empires, Tantalus Media, Wicked Witch', N'Estratégia em tempo real', N'Age of Empires II: Definitive Edition'),
(44, '2020-09-17T00:00:00.0000000', N'Hades é um jogo de ação e roguelike desenvolvido pela Supergiant Games. Os jogadores assumem o papel de Zagreus, filho do deus grego Hades, enquanto ele tenta escapar do Submundo. Com uma jogabilidade fluida, combates desafiadores e uma narrativa envolvente, Hades oferece uma experiência cativante com elementos mitológicos.', N'Supergiant Games', N'Ação, Roguelike', N'Hades'),
(45, '2017-09-29T00:00:00.0000000', N'Cuphead é um jogo de plataforma e ação desenvolvido pelo Studio MDHR. Com uma estética inspirada em desenhos animados dos anos 1930, os jogadores controlam Cuphead ou Mugman em batalhas contra chefes desafiadores. Com uma jogabilidade precisa, visuais impressionantes e trilha sonora envolvente, Cuphead oferece uma experiência única e desafiadora.', N'Studio MDHR', N'Plataforma, Ação', N'Cuphead'),
(46, '2019-01-25T00:00:00.0000000', N'Resident Evil 2 Remake é um jogo de terror e sobrevivência desenvolvido pela Capcom. O jogo é uma recriação do clássico Resident Evil 2 lançado em 1998. Os jogadores assumem o papel de Leon S. Kennedy e Claire Redfield, enquanto exploram a cidade infestada por zumbis. Com gráficos realistas, jogabilidade modernizada e uma atmosfera tensa, Resident Evil 2 Remake oferece uma experiência assustadora e envolvente.', N'Capcom', N'Terror, Sobrevivência', N'Resident Evil 2 Remake'),
(47, '2017-02-23T00:00:00.0000000', N'NieR: Automata é um jogo de ação e RPG desenvolvido pela PlatinumGames. Situado em um futuro distópico, os jogadores controlam androides de combate em uma guerra contra máquinas. Com uma narrativa complexa, jogabilidade fluida e uma trilha sonora marcante, NieR: Automata oferece uma experiência emocional e envolvente.', N'PlatinumGames', N'Ação, RPG', N'NieR: Automata'),
(48, '2016-06-14T00:00:00.0000000', N'Dead by Daylight é um jogo de terror multiplayer assimétrico desenvolvido pela Behaviour Interactive. Os jogadores podem assumir o papel de um assassino sanguinário ou de um dos sobreviventes tentando escapar. Com uma jogabilidade única e emocionante, Dead by Daylight oferece uma experiência intensa de perseguição e sobrevivência.', N'Behaviour Interactive', N'Terror, Multijogador', N'Dead by Daylight'),
(49, '2019-11-08T00:00:00.0000000', N'Need for Speed: Heat é um jogo de corrida desenvolvido pela Ghost Games. Ambientado em Palm City, os jogadores participam de corridas ilegais durante o dia para ganhar dinheiro e aprimorar seus carros, e corridas emocionantes à noite para aumentar sua reputação. Com uma ampla seleção de carros, personalização detalhada e perseguições policiais intensas, Need for Speed: Heat oferece uma experiência emocionante para os fãs de corrida.', N'Ghost Games', N'Corrida', N'Need for Speed: Heat'),
(50, '2019-10-04T00:00:00.0000000', N'Tom Clancy''s Ghost Recon Breakpoint é um jogo de tiro em mundo aberto desenvolvido pela Ubisoft. Situado em uma ilha fictícia chamada Auroa, os jogadores assumem o papel de um soldado de elite lutando contra um exército mercenário. Com uma jogabilidade tática, elementos de sobrevivência e um modo cooperativo online, Ghost Recon Breakpoint oferece uma experiência de combate emocionante.', N'Ubisoft', N'Tiro, Mundo Aberto', N'Tom Clancy''s Ghost Recon Breakpoint'),
(51, '2020-03-20T00:00:00.0000000', N'Doom Eternal é um jogo de tiro em primeira pessoa desenvolvido pela id Software. Os jogadores assumem o papel do Doom Slayer, um guerreiro enfrentando hordas de demônios do inferno. Com uma jogabilidade frenética, armas poderosas e combates intensos, Doom Eternal oferece uma experiência visceral e cheia de ação.', N'id Software', N'Tiro em primeira pessoa', N'Doom Eternal'),
(52, '2011-08-23T00:00:00.0000000', N'Deus Ex: Human Revolution é um jogo de ação e RPG desenvolvido pela Eidos Montreal. Ambientado em um futuro distópico, os jogadores assumem o papel de Adam Jensen, um segurança cibernético com habilidades aprimoradas. Com uma narrativa envolvente, escolhas morais impactantes e jogabilidade que combina ação furtiva e combate, Deus Ex: Human Revolution oferece uma experiência imersiva e cativante.', N'Eidos Montreal', N'Ação, RPG', N'Deus Ex: Human Revolution'),
(53, '2019-09-13T00:00:00.0000000', N'Borderlands 3 é um jogo de tiro em primeira pessoa e RPG de ação desenvolvido pela Gearbox Software. Os jogadores exploram mundos diversos, enfrentam inimigos e completam missões em busca de tesouros e loot. Com um estilo visual único, um elenco de personagens memoráveis e um sistema de armas variado, Borderlands 3 oferece uma experiência cheia de humor e caos.', N'Gearbox Software', N'Tiro em primeira pessoa, RPG de ação', N'Borderlands 3'),
(54, '2019-03-22T00:00:00.0000000', N'Sekiro: Shadows Die Twice é um jogo de ação e aventura desenvolvido pela FromSoftware. Ambientado no Japão feudal, os jogadores assumem o papel de um guerreiro ninja em busca de vingança. Com combates desafiadores, habilidades especiais e uma narrativa envolvente, Sekiro: Shadows Die Twice oferece uma experiência emocionante e exigente.', N'FromSoftware', N'Ação, Aventura', N'Sekiro: Shadows Die Twice'),
(55, '2020-03-23T00:00:00.0000000', N'Half-Life: Alyx é um jogo de realidade virtual desenvolvido pela Valve. Situado no universo de Half-Life, os jogadores assumem o papel de Alyx Vance em uma missão para combater uma invasão alienígena. Com uma narrativa envolvente, jogabilidade imersiva em realidade virtual e quebra-cabeças desafiadores, Half-Life: Alyx redefine a experiência de jogos em VR.', N'Valve', N'Ação, Aventura, Realidade Virtual', N'Half-Life: Alyx'),
(56, '2019-08-27T00:00:00.0000000', N'Control é um jogo de ação e aventura desenvolvido pela Remedy Entertainment. Os jogadores assumem o papel de Jesse Faden, uma mulher com habilidades sobrenaturais, enquanto ela explora um prédio misterioso chamado Oldest House. Com poderes paranormais, combates intensos e uma narrativa intrigante, Control oferece uma experiência única e envolvente.', N'Remedy Entertainment', N'Ação, Aventura', N'Control'),
(57, '2016-10-21T00:00:00.0000000', N'Battlefield 1 é um jogo de tiro em primeira pessoa desenvolvido pela DICE. Ambientado na Primeira Guerra Mundial, os jogadores participam de batalhas épicas em diversos cenários históricos. Com um modo campanha emocionante e um modo multiplayer intenso, Battlefield 1 oferece uma experiência imersiva e repleta de ação.', N'DICE', N'Tiro em primeira pessoa', N'Battlefield 1'),
(58, '2019-03-15T00:00:00.0000000', N'Tom Clancy''s The Division 2 é um jogo de tiro em terceira pessoa desenvolvido pela Massive Entertainment. Ambientado em um mundo pós-apocalíptico, os jogadores fazem parte de uma força especial para restaurar a ordem e combater facções hostis. Com um vasto mundo aberto, combates estratégicos e modo multiplayer cooperativo, The Division 2 oferece uma experiência completa e dinâmica.', N'Massive Entertainment', N'Tiro em terceira pessoa, RPG de ação', N'Tom Clancy''s The Division 2'),
(59, '2021-01-20T00:00:00.0000000', N'Hitman 3 é um jogo de stealth e ação desenvolvido pela IO Interactive. Como o Agente 47, os jogadores viajam pelo mundo para eliminar alvos usando criatividade, disfarces e habilidades furtivas. Com uma variedade de missões e locais impressionantes, Hitman 3 oferece uma experiência envolvente e desafiadora para os fãs da série.', N'IO Interactive', N'Ação, Stealth', N'Hitman 3'),
(60, '2013-08-27T00:00:00.0000000', N'Final Fantasy XIV é um MMORPG desenvolvido pela Square Enix. Os jogadores embarcam em aventuras épicas em um mundo de fantasia, enfrentando monstros, explorando masmorras e participando de batalhas emocionantes. Com uma história rica, personagens memoráveis e um sistema de classes flexível, Final Fantasy XIV oferece uma experiência online cativante para os fãs da franquia.', N'Square Enix', N'MMORPG', N'Final Fantasy XIV'),
(61, '2021-05-14T00:00:00.0000000', N'Mass Effect: Legendary Edition é uma coletânea remasterizada dos jogos da trilogia Mass Effect, desenvolvida pela BioWare. Os jogadores embarcam em uma jornada épica pelo espaço como o Comandante Shepard, tomando decisões que afetam o destino da galáxia. Com gráficos aprimorados, jogabilidade refinada e todas as DLCs inclusas, Mass Effect: Legendary Edition oferece uma experiência imersiva e emocionante.', N'BioWare', N'RPG de ação, Ficção científica', N'Mass Effect: Legendary Edition'),
(62, '2017-09-06T00:00:00.0000000', N'Destiny 2 é um jogo de tiro em primeira pessoa desenvolvido pela Bungie. Os jogadores exploram o universo fictício e lutam contra inimigos em uma história épica. Com uma campanha envolvente, modos PvP competitivos e desafios PvE cooperativos, Destiny 2 oferece uma experiência online dinâmica e repleta de ação.', N'Bungie', N'Tiro em primeira pessoa, MMO', N'Destiny 2'),
(63, '2020-04-03T00:00:00.0000000', N'Resident Evil 3 Remake é uma reimaginação do clássico jogo de terror desenvolvido pela Capcom. Os jogadores assumem o papel de Jill Valentine enquanto tentam sobreviver a um apocalipse zumbi. Com gráficos realistas, jogabilidade intensa e uma narrativa envolvente, Resident Evil 3 Remake oferece uma experiência assustadora e emocionante.', N'Capcom', N'Survival horror', N'Resident Evil 3 Remake'),
(64, '2006-03-20T00:00:00.0000000', N'The Elder Scrolls IV: Oblivion é um RPG de mundo aberto desenvolvido pela Bethesda Game Studios. Os jogadores exploram a província de Cyrodiil enquanto embarcam em uma jornada para salvar o reino de Tamriel de uma ameaça demoníaca. Com um vasto mundo, personagens cativantes e uma jogabilidade envolvente, Oblivion é um clássico dos jogos de RPG.', N'Bethesda Game Studios', N'RPG de ação, Mundo aberto', N'The Elder Scrolls IV: Oblivion'),
(65, '2015-03-10T00:00:00.0000000', N'Cities: Skylines é um jogo de construção de cidades desenvolvido pela Colossal Order. Os jogadores têm a tarefa de projetar, construir e gerenciar uma cidade em crescimento, cuidando de aspectos como infraestrutura, serviços públicos e economia. Com uma ampla gama de opções e ferramentas, Cities: Skylines oferece uma experiência detalhada e imersiva para os fãs de simulação urbana.', N'Colossal Order', N'Simulação, Construção', N'Cities: Skylines'),
(66, '2015-06-23T00:00:00.0000000', N'Batman: Arkham Knight é um jogo de ação e aventura desenvolvido pela Rocksteady Studios. Os jogadores assumem o papel do Batman enquanto lutam contra os vilões de Gotham City. Com um combate fluido, um enredo envolvente e gráficos impressionantes, Arkham Knight oferece uma experiência imersiva no mundo do Cavaleiro das Trevas.', N'Rocksteady Studios', N'Ação, Aventura', N'Batman: Arkham Knight'),
(67, '2018-05-25T00:00:00.0000000', N'Detroit: Become Human é um jogo de aventura interativa desenvolvido pela Quantic Dream. Os jogadores assumem o controle de androides em um futuro distópico, tomando decisões que afetam o desenrolar da história. Com uma narrativa complexa e ramificada, gráficos impressionantes e temáticas profundas, Detroit: Become Human oferece uma experiência única e emocional.', N'Quantic Dream', N'Aventura, Drama', N'Detroit: Become Human'),
(68, '2020-03-11T00:00:00.0000000', N'Ori and the Will of the Wisps é um jogo de plataforma e aventura desenvolvido pela Moon Studios. Os jogadores assumem o controle de Ori, um espírito guardião, em sua jornada para descobrir seu destino e salvar a floresta de Nibel. Com visuais deslumbrantes, uma trilha sonora envolvente e mecânicas de jogo refinadas, Ori and the Will of the Wisps é uma experiência emocionante e cativante.', N'Moon Studios', N'Plataforma, Aventura', N'Ori and the Will of the Wisps'),
(69, '2016-10-21T00:00:00.0000000', N'Sid Meier''s Civilization VI é um jogo de estratégia por turnos desenvolvido pela Firaxis Games. Os jogadores assumem o papel de líderes de civilizações e devem expandir seus impérios, pesquisar tecnologias, negociar com outras nações e competir pelo domínio global. Com uma jogabilidade profunda, mecânicas desafiadoras e uma rica história, Civilization VI é uma referência no gênero de estratégia.', N'Firaxis Games', N'Estratégia', N'Sid Meier''s Civilization VI'),
(70, '2018-05-08T00:00:00.0000000', N'Pillars of Eternity II: Deadfire é um RPG de fantasia desenvolvido pela Obsidian Entertainment. Os jogadores embarcam em uma jornada épica pelos mares, explorando ilhas exóticas, enfrentando criaturas perigosas e tomando decisões que afetam o destino do mundo. Com um sistema de combate tático, uma narrativa envolvente e uma vasta quantidade de escolhas, Pillars of Eternity II oferece uma experiência imersiva para os fãs de RPG.', N'Obsidian Entertainment', N'RPG', N'Pillars of Eternity II: Deadfire'),
(71, '2018-02-13T00:00:00.0000000', N'Kingdom Come: Deliverance é um RPG de mundo aberto realista desenvolvido pela Warhorse Studios. Os jogadores assumem o papel de Henry, um simples camponês que se torna envolvido em uma guerra civil na Boêmia do século XV. Com um foco em realismo histórico, combate desafiador e uma narrativa imersiva, Kingdom Come: Deliverance oferece uma experiência única e envolvente para os fãs de RPG.', N'Warhorse Studios', N'RPG de ação, Mundo aberto', N'Kingdom Come: Deliverance'),
(72, '2016-11-11T00:00:00.0000000', N'Dishonored 2 é um jogo de ação e aventura desenvolvido pela Arkane Studios. Os jogadores assumem o papel de Emily Kaldwin ou Corvo Attano, personagens com habilidades sobrenaturais, em uma missão para restaurar a justiça em uma cidade dominada pela corrupção. Com um sistema de combate flexível, um mundo rico em detalhes e múltiplas abordagens para cada situação, Dishonored 2 oferece liberdade e escolhas para os jogadores.', N'Arkane Studios', N'Ação, Aventura', N'Dishonored 2'),
